/* English/UK initialisation for the jQuery UI date picker plugin. */
/* Written by Stuart. */
jQuery(function($){
	$.timepicker.regional['en'] = {
		closeText: 'Close',
		currentText: 'Today',
		timeOnlyTitle: 'Give time',
		timeText: 'Time',
		hourText: 'Hour',
		minuteText: 'Minute',
		secondText: 'Second'	
		};
	$.timepicker.setDefaults($.timepicker.regional['en']);
});